mapper.update:
 	 /* User.updateUserSalt */
 	UPDATE TB_MBR_INFO_M
	SET salt_vl = #{salt_vl}
	WHERE mbr_sn = #{mbr_sn}

mapper.select:
 	 /* User.selectMail */
	SELECT rtnCode
	FROM (
		SELECT 'Y' rtnCode, COUNT(*) cnt FROM TB_MBR_INFO_M WHERE eml_addr = #{eml_addr}
		UNION 
		SELECT 'N' rtnCode, 1
	) a
	where cnt > 0
	order by rtnCode desc
	limit 1

 	 /* User.selectUserId */
	SELECT rtnCode, rtnMsg
	FROM (
		SELECT '1' rtnCode, COUNT(*) cnt, eml_addr rtnMsg FROM TB_MBR_INFO_M WHERE mbr_tel = #{mbr_tel}
		UNION 
		SELECT '0' rtnCode, 1, '해당 번호와 일치하는 아이디가 존재하지 않습니다' rtnMsg
	) a
	where cnt > 0
	order by rtnCode desc
	limit 1

mapper.update:
 	 /* User.updateUserTempPswd */
	UPDATE TB_MBR_INFO_M
	SET pswd = #{pswd}
	, pswd_init_yn = 'Y'
	WHERE eml_addr = #{eml_addr}

mapper.select:
 	 /* User.selectUsedPswd */
	SELECT rtnCode
	FROM (
		SELECT 'Y' rtnCode, COUNT(*) cnt FROM TB_MBR_INFO_M WHERE pswd = #{temp_pswd} AND eml_addr = #{eml_addr}
		UNION 
		SELECT 'N' rtnCode, 1
	) a
	where cnt > 0
	order by rtnCode desc
	limit 1

mapper.update:
 	 /* User.updateUserPswd */
	UPDATE TB_MBR_INFO_M
	SET pswd = #{pswd}
	, pswd_init_yn = 'N'
	WHERE pswd = #{temp_pswd}
	AND eml_addr = #{eml_addr}

 	 /* User.updateUserResign */
	UPDATE TB_MBR_INFO_M
	SET eml_addr = CONCAT(#{eml_addr},'_')
	, resign_yn = 'Y'
	WHERE eml_addr = #{eml_addr}
	AND salt_vl = #{salt_vl}

mapper.select:
 /* User.selectUserInfo */
SELECT * 
FROM TB_MBR_INFO_M
WHERE mbr_sn = #{mbr_sn}

mapper.update:
 	 /* User.updateUserDetail */
 	UPDATE TB_MBR_INFO_M


	 	mbr_name = #{mbr_name},


		mbr_type = #{mbr_type},


		pswd = #{pswd},


 	WHERE mbr_sn= #{mbr_sn}

mapper.select:
 	 /* User.selectUserList */
 	 SELECT *
	 	 , (
 	 		SELECT COUNT(*) 
		 	FROM TB_MBR_INFO_M


				 	AND mbr_sn = #{mbr_sn}


				 	AND mbr_id = #{mbr_id}


				 	AND mbr_name = #{mbr_name}


				 	AND eml_addr = #{eml_addr}


				 	AND mbr_tel = #{mbr_tel}


				 	AND (
				 	eml_addr LIKE '%${searchText}%'
				 	OR mbr_name LIKE '%${searchText}%'
				 	OR eml_addr LIKE '%${searchText}%'
				 	)


 		 ) TOTAL
 	 FROM (
	 	SELECT @ROWNUM:=@ROWNUM+1 AS ROWNUM, m.* 
	 	FROM TB_MBR_INFO_M m, (SELECT @ROWNUM:=0) r 


			 	AND mbr_sn = #{mbr_sn}


			 	AND mbr_id = #{mbr_id}


			 	AND mbr_name = #{mbr_name}


			 	AND eml_addr = #{eml_addr}


			 	AND mbr_tel = #{mbr_tel}


			 	AND (
			 	eml_addr LIKE '%${searchText}%'
			 	OR mbr_name LIKE '%${searchText}%'
			 	OR eml_addr LIKE '%${searchText}%'
			 	)


		order by m.mbr_sn desc
 	 ) P

		LIMIT #{PAGE_SIZE}


		OFFSET #{CURRENT_START}
